INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (1, 'Cien años de soledad', 'Gabriel García Márquez', 'Sudamericana', '1967-05-30', 432, 'Español', 'Ficción', 'Libro', 'La novela relata la historia de la familia Buendía a lo largo de siete generaciones en el ficticio pueblo de Macondo.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (2, 'El amor en los tiempos del cólera', 'Gabriel García Márquez', 'Sudamericana', '1985-11-01', 368, 'Español', 'Ficción', 'Libro', 'La novela cuenta la historia de amor entre Florentino Ariza y Fermina Daza, que se desarrolla a lo largo de más de cincuenta años en la ciudad de Cartagena.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (3, '1984', 'George Orwell', 'Secker & Warburg', '1949-06-08', 328, 'Inglés', 'Ficción', 'Libro', 'La novela presenta un mundo totalitario en el que el gobierno controla todos los aspectos de la vida de las personas.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (4, 'Rebelión en la granja', 'George Orwell', 'Secker & Warburg', '1945-08-17', 112, 'Inglés', 'Ficción', 'Libro', 'La novela satiriza la Revolución rusa y el surgimiento del régimen estalinista en la Unión Soviética.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (5, 'El Aleph', 'Jorge Luis Borges', 'Losada', '1949-06-08', 224, 'Español', 'Ficción', 'Libro', 'La obra recopila cuentos que exploran temas como el tiempo, el infinito y la realidad.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (6, 'Rayuela', 'Julio Cortázar', 'Sudamericana', '1963-06-28', 736, 'Español', 'Ficción', 'Libro', 'La novela presenta una estructura no lineal y está compuesta de varios capítulos que se pueden leer en diferentes órdenes.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (7, 'La insoportable levedad del ser', 'Milan Kundera', 'Galiani', '1984-11-28', 352, 'Español', 'Ficción', 'Libro', 'La novela explora las relaciones humanas, el amor, la libertad y la política a través de la historia de cuatro personajes.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (8, 'La Odisea', 'Homero', 'Desconocida', '1998-10-27', 416, 'Griego', 'Poesía épica', 'Libro', 'La obra cuenta las aventuras del héroe griego Odiseo en su regreso a casa después de la Guerra de Troya.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (9, 'Don Quijote de la Mancha', 'Miguel de Cervantes', 'Francisco de Robles', '1605-01-16', 863, 'Español', 'Novela', 'Libro', 'La obra cuenta las aventuras del hidalgo Alonso Quixano, quien se cree un caballero andante y sale a recorrer el mundo en busca de aventuras.');
INSERT INTO dbo.Bibliografia (id_bibliografia, titulo, autor, editorial, fecha_publicacion, numero_paginas, idioma, categoria, formato, resumen) VALUES (10, 'Crónica de una muerte anunciada', 'Gabriel García Márquez', 'Sudamericana', '1981-09-01', 120, 'Español', 'Ficción', 'Libro', 'La novela relata el asesinato de Santiago Nasar y la inminencia de su muerte, que es anunciada por los habitantes del pueblo en el que vive.');
